<?php

declare(strict_types=1);

namespace JacobDeKeizer\Ccv\Endpoints;

use JacobDeKeizer\Ccv\Exceptions\CcvShopException;

/**
 * This class is autogenerated.
 */
class InvoicelabelsEndpoint extends BaseEndpoint
{
    /**
     * Get all labels linked to this invoice. 150 per minute
     *
     * @throws CcvShopException
     */
    public function allFromInvoice(int $id): \JacobDeKeizer\Ccv\Models\Vnd\Verto\Webshop\Resource\Collection\Invoicelabels\Invoicelabels
    {
        $result = $this->doRequest(
            self::GET,
            'invoices/' . $id . '/invoicelabels/'
        );

        return \JacobDeKeizer\Ccv\Models\Vnd\Verto\Webshop\Resource\Collection\Invoicelabels\Invoicelabels::fromArray($result);
    }

    /**
     * Replaces all labels of this invoice with the new collection. An empty collection can be used to remove all labels. 100 per minute
     *
     * @throws CcvShopException
     */
    public function updateForInvoice(int $id, \JacobDeKeizer\Ccv\Models\Internal\Resource\Invoicelabels\Put\Put $model, bool $onlyFilled = true): void
    {
        $this->doRequest(
            self::PUT,
            'invoices/' . $id . '/invoicelabels/',
            $model->toArray($onlyFilled)
        );
    }
}
