<?php

declare(strict_types=1);

namespace JacobDeKeizer\Ccv\Endpoints;

use JacobDeKeizer\Ccv\Exceptions\CcvShopException;

/**
 * This class is autogenerated.
 */
class OrderlabelsEndpoint extends BaseEndpoint
{
    /**
     * Get all labels linked to this order. 150 per minute
     *
     * @throws CcvShopException
     */
    public function allFromOrder(int $id): \JacobDeKeizer\Ccv\Models\Vnd\Verto\Webshop\Resource\Collection\Orderlabels\Orderlabels
    {
        $result = $this->doRequest(
            self::GET,
            'orders/' . $id . '/orderlabels/'
        );

        return \JacobDeKeizer\Ccv\Models\Vnd\Verto\Webshop\Resource\Collection\Orderlabels\Orderlabels::fromArray($result);
    }

    /**
     * Replaces all labels of this order with the new collection. An empty collection can be used to remove all labels. 100 per minute
     *
     * @throws CcvShopException
     */
    public function updateForOrder(int $id, \JacobDeKeizer\Ccv\Models\Internal\Resource\Orderlabels\Put\Put $model, bool $onlyFilled = true): void
    {
        $this->doRequest(
            self::PUT,
            'orders/' . $id . '/orderlabels/',
            $model->toArray($onlyFilled)
        );
    }
}
